INSERT INTO `tb_aktivitas` VALUES (15, 'Kontes Resep Kue Kering', ' Cookie Recipe Contest', ' Cookie Recipe Contest_Kontes Resep Kue Kering_24102024224447.jpg', '<p>Ajak pelanggan untuk mengirimkan resep kue kering kreatif mereka. Pilih pemenang setiap bulan dan sajikan resep mereka di situs web serta hadiah seperti paket kue kering gratis.</p>', '<p>&nbsp;Invite customers to submit their creative cookie recipes. Choose a winner each month and feature their recipe on the website along with a prize such as a free cookie gift pack.</p>', 'Sofi Cookies | Detail Aktivitas', 'activity01', 'kontes-resep-kue-kering', 'cookie-recipe-contest', 'Sofi Cookies | Activity Details', 'activity01');
INSERT INTO `tb_aktivitas` VALUES (16, 'Sesi Live Baking', 'Live Baking Sessions', 'Live Baking Sessions_Sesi Live Baking_24102024230831.jpg', '<p>Ikuti sesi live baking bersama chef kami! Pelajari cara membuat kue kering khas Sofi Cookies langsung dari dapur kami. Tanyakan pertanyaan Anda dan dapatkan tips memasak yang berguna!</p>', '<p>Join us for a live baking session with our chef! Learn how to make Sofi Cookies\' signature cookies directly from our kitchen. Ask your questions and get useful baking tips!</p>', 'Sofi Cookies | Detail Aktivitas', 'Live Baking Sessions', 'sesi-live-baking', 'live-baking-sessions', 'Sofi Cookies | Activity Details', 'Live Baking Sessions');
INSERT INTO `tb_aktivitas` VALUES (17, 'Kalender Acara dan Promo Spesial', 'Event Calendar and Special Promotions', 'Event Calendar and Special Promotions_Kalender Acara dan Promo Spesial_24102024232053.jpg', '<p>Cek kalender kami untuk melihat acara mendatang dan penawaran spesial! Dapatkan diskon musiman, ikuti acara komunitas, dan temukan pop-up store di area Anda.</p>\r\n<p><strong>Kegiatan Mendatang</strong>:</p>\r\n<ul>\r\n<li>Diskon 20% untuk pembelian online di akhir bulan ini!</li>\r\n</ul>', '<p>Check our calendar to see upcoming events and special offers! Get seasonal discounts, participate in community events, and find pop-up stores in your area.</p>\r\n<p><strong>Upcoming Activities</strong>:</p>\r\n<ul>\r\n<li>20% discount on online purchases at the end of this month!</li>\r\n</ul>', 'Sofi Cookies | Detail Aktivitas', 'Event Calendar and Special Promotions', 'kalender-acara-dan-promo-spesial', 'event-calendar-and-special-promotions', 'Sofi Cookies | Activity Details', 'Event Calendar and Special Promotions');

